script:
{%- set idx = {} -%}
{%- set _do = idx.update({
  "record": {
    "devname": devmeta.hostname,
    "comment": "Changed " + record.devname + " to " + devmeta.hostname + "."
  }
}) -%}
{{idx}}

{%- set vlan_count = devmeta.vlan_count|int -%}
{%- set site_subnet = devmeta.site_subnet -%}
{%- set vlan_cidr = devmeta.vlan_cidr|int %}
{%- set i = namespace(x=0) -%}
{%- for vlan in range(2,2+vlan_count) %}
  {%- set lan_block = site_subnet | ipaddr('subnet') | ipsubnet(vlan_cidr,i.x) -%}
config system interface
  edit "VLAN{{vlan}}"
    set vdom "root"
    set ip {{lan_block|ipaddr(1)|ipaddr('address')}} {{lan_block|ipaddr('netmask')}}
    set allowaccess ping ssh
    set interface "fortilink"
    set vlanid {{vlan}}
    set description "FortiSOAR Script run for DeviceID {{record.id}}"
  next
end
  {% set i.x = i.x+1 -%}
{% endfor %}

{%- set mysupernet = devmeta.supernet|default('10.0.0.0/8') -%}
{%- set mycidr = devmeta.cidr|default(32) -%}
{%- set mystart_index = devmeta.start_index|default(1) -%}
{%- set mynetwork_count = devmeta.network_count|default(4) -%}

{%- set networks = [] -%}
{%- set i = namespace(x=0) -%}
{%- for n in range(mystart_index,mynetwork_count+1) %}
  {%- set mynet = mysupernet | ipaddr('subnet') | ipsubnet(mycidr,n) -%}
  {%- if mynet -%}
    {%- set _do = networks.append(mynet) -%}
  {%- endif -%}
  {%- set i.x = i.x+1 -%}
{%- endfor -%}
config firewall address
{%- for a in networks %}
  edit "{{a}}"
    set subnet {{a}}
    set comment "Created by FortiSOAR ZTPF Script"
  next
{%- endfor %}
end
config firewall policy
purge
{%- set e = namespace(x=1) -%}
{%- for src in networks %}
  {%- for dst in networks %}
    {%- if src != dst %}
  edit {{e.x}}
    set name "{{src}}-to-{{dst}}"
    set comment "Created by FortiSOAR ZTPF Script"
    set srcaddr {{src}}
    set dstaddr {{dst}}
    set srcintf "any"
    set dstintf "any"
    set service "ALL"
    set action accept
    set schedule "always"
    set logtraffic all
  next
    {%- set e.x = e.x+1 %}
    {%- endif %}
  {%- endfor %}
{%- endfor %}
end

{%- set loopback_supernet = "172.16.30.0/23" -%}
{%- set netindex = site_id|int -%}
{%- set loopback_ip = loopback_supernet|ipsubnet(32,netindex-1) -%}
config system interface
  edit lo1
    set vdom root
    set type loopback
    set ip {{loopback_ip}}
  next
end

{%- set table = [] -%}
{%- for i in interfaces -%}
  {%- set rec = {} -%}
  {%- if "ip" in i -%}
    {%- if i.ip[0] != "0.0.0.0" -%}
      {%- set subnet = ( i.ip[0] + "/" + i.ip[1] ) | ipsubnet -%}
      {%- set intip = subnet|ipaddr('cidr') -%}
    {%- else -%}
      {%- set intip = i.ip[0] -%}
    {%- endif -%}
  {%- else -%}
    {%- set intip = "no ip" -%}
  {%- endif -%}  
  {%- set _do = rec.update({
              "name":i.name,
              "description":i.description,
              "type":i.type,
              "ip": intip
  }) -%}    
  {%- set _do = table.append(rec) -%}
{%- endfor -%}

#### Interfaces

| Interface | Type | IP Address | Description |
| ---------- | ------ | ------------  | ------------- |
{% for r in table -%}
| {{r.name}} | {{r.type}} | {{r.ip}} | {{r.description}} |
{% endfor -%}

{%- set table = [] -%}
{%- for i in interfaces -%}
  {%- if "physical" in i.type -%}
  {%- set rec = {} -%}
  {%- if "ip" in i -%}
    {%- if i.ip[0] != "0.0.0.0" -%}
      {%- set subnet = ( i.ip[0] + "/" + i.ip[1] ) | ipsubnet -%}
      {%- set intip = subnet|ipaddr('cidr') -%}
    {%- else -%}
      {%- set intip = i.ip[0] -%}
    {%- endif -%}
  {%- else -%}
    {%- set intip = "no ip" -%}
  {%- endif -%}  
  {%- set _do = rec.update({
              "name":i.name,
              "description":i.description,
              "oid":i.oid,
              "ip": intip,
              "status":i.status
  }) -%}    
  {%- set _do = table.append(rec) -%}
  {%- endif -%}
{%- endfor -%}

